import React, { useCallback, useEffect, useState } from 'react';
import {
  Button,
  FormControl,
  FormLabel,
  Input,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  useDisclosure,
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverArrow,
  PopoverCloseButton,
  PopoverBody,
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  Box,
  AccordionIcon,
  Drawer,
  DrawerOverlay,
  DrawerContent,
  DrawerFooter,
  DrawerBody,
  DrawerCloseButton,
  DrawerHeader
} from '@chakra-ui/react';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import useCoveyAppState from '../../hooks/useCoveyAppState';
import Player from '../../classes/Player';
// import './App.css';



const Chat: React.FunctionComponent = () => {
  const {isOpen, onOpen, onClose} = useDisclosure()
  const {socket, messageHistory, players, userName, unseenMessages} = useCoveyAppState();
  const [message, setMessage] = useState<string>('');
  const [currentPlayers, setCurrentPlayers] = useState<Player[]>([]);
  const [receiver, setReceiver] = useState<string>('');

  const openChat = useCallback(()=>{
    onOpen();
    socket?.emit('looking at messages');
  }, [onOpen, socket]);

  const closeChat = useCallback(()=>{
    onClose();
    socket?.emit('looking at messages');
  }, [onClose, socket]);


  const updatePlayers = useCallback(() => {
    setCurrentPlayers(players.sort((a,b) => a.userName < b.userName ? -1:1));
  }, [players]);


  useEffect(() => {
    updatePlayers();
    // updateUnseen();
    const timer = setInterval(updatePlayers, 2000);
    // const timer3 = setInterval(updateUnseen, 2000);
    // const timer2 = setInterval(updateMessageHistory, 2000);

    return () => {
      clearInterval(timer);
      // clearInterval(timer2);
      // clearInterval(timer3);
    };
  }, [updatePlayers]);

  const processUpdates = async () =>{
    if (receiver === 'Everyone') {
      socket?.emit('sendPublicMessage', message, userName);
    } else {
      socket?.emit('sendPrivateMessage', message, receiver, userName)
    } 
  };
  const btnRef = React.useRef()

  return <>
    <MenuItem data-testid='openMenuButton' onClick={openChat}>
      <Typography variant="body1">Chat</Typography>
    </MenuItem>
{/* <Button ref={btnRef} colorScheme="teal" onClick={onOpen}>
        Open
      </Button> */}
      <Drawer
        isOpen={isOpen}
        placement="right"
        onClose={onClose}
        // finalFocusRef={btnRef}
      >
        <DrawerOverlay>
          <DrawerContent>
            <DrawerCloseButton />
            <DrawerHeader>Create your account</DrawerHeader>

            <DrawerBody>
              <Input placeholder="Type here..." />
            </DrawerBody>

            <DrawerFooter>
              <Button variant="outline" mr={3} onClick={onClose}>
                Cancel
              </Button>
              <Button colorScheme="blue">Save</Button>
            </DrawerFooter>
          </DrawerContent>
        </DrawerOverlay>
      </Drawer>
    {/* <Modal isOpen={isOpen} onClose={closeChat}>
      <ModalContent>
        <ModalHeader>Chat</ModalHeader>
        <ModalCloseButton/>
          <ModalBody pb={6}>

            {currentPlayers?.map((player) => ( 
              <p key={player.id}> {player.userName} </p>))}  

            <div>
            {messageHistory?.messageHistory.map((msg) => ( 
              <p key={msg.id}> {msg.from} to {msg.to} <br /> {msg.message}  </p>
            ))} 
            </div>
          </ModalBody>
          <ModalFooter>
          <FormControl>
            <FormLabel htmlFor='friendlyName'>To</FormLabel>
              <Input id='friendlyName' placeholder="To" name="receiver" value={receiver} onChange={(ev)=>setReceiver(ev.target.value)} />
            </FormControl>
            <FormControl>
              <Input id='message' placeholder="Message..." name="message" value={message} onChange={(ev)=>setMessage(ev.target.value)} />
            </FormControl>
            <Button data-testid='sendbutton' colorScheme="blue" mr={3} value="send" name='action1' onClick={()=>processUpdates()}>
              Send
            </Button>
          </ModalFooter>
      </ModalContent>
    </Modal> */}
  </>
}

export default Chat;
