import React, { useCallback, useState } from 'react';
import Draggable from 'react-draggable';

import {
  Button,
  Checkbox,
  FormControl,
  FormLabel,
  Input,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  useToast
} from '@chakra-ui/react';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import useCoveyAppState from '../../hooks/useCoveyAppState';
// import useMaybeVideo from '../../hooks/useMaybeVideo';

const Chat: React.FunctionComponent = () => {
  const {isOpen, onOpen, onClose} = useDisclosure()
//   const video = useMaybeVideo()
  const {apiClient, currentTownID, currentTownFriendlyName, currentTownIsPubliclyListed, socket, messageHistory} = useCoveyAppState();
  const [friendlyName, setFriendlyName] = useState<string>(currentTownFriendlyName);
  const [message, setMessage] = useState<string>('');
  const [isPubliclyListed, setIsPubliclyListed] = useState<boolean>(currentTownIsPubliclyListed);
  const [roomUpdatePassword, setRoomUpdatePassword] = useState<string>('');



  const openChat = useCallback(()=>{
    onOpen();
  }, [onOpen]);

  const closeChat = useCallback(()=>{
    onClose();
  }, [onClose]);

  const toast = useToast()
  const processUpdates = async (action: string) =>{
    if(action === 'send'){
      console.log(message);
      socket?.emit('publicMessage', message)
        // closeSettings();

    }else {
      try {
        await apiClient.updateTown({
          coveyTownID: currentTownID,
          coveyTownPassword: roomUpdatePassword,
          friendlyName,
          isPubliclyListed
        });
        toast({
          title: 'Town updated',
          description: 'To see the updated town, please exit and re-join this town',
          status: 'success'
        })
        // closeSettings();
      }catch(err){
        toast({
          title: 'Unable to update town',
          description: err.toString(),
          status: 'error'
        });
      }
    }
  };

  return <>
    <MenuItem data-testid='openMenuButton' onClick={openChat}>
      <Typography variant="body1">Chat</Typography>
    </MenuItem>
    <Modal isOpen={isOpen} onClose={closeChat}>
      <ModalOverlay/>
      <ModalContent>
        <ModalHeader>Chat</ModalHeader>
        <ModalCloseButton/>
        <form onSubmit={(ev)=>{ev.preventDefault(); processUpdates('edit')}}>
          <ModalBody pb={6}>
            <FormControl>
              <FormLabel htmlFor='friendlyName'>{messageHistory}</FormLabel>
              <Input id='friendlyName' placeholder="Friendly Name" name="friendlyName" value={friendlyName} onChange={(ev)=>setFriendlyName(ev.target.value)} />
            </FormControl>
            <FormControl isRequired>
              <FormLabel htmlFor="updatePassword">Town Update Password</FormLabel>
              <Input data-testid="updatePassword" id="updatePassword" placeholder="Password" name="password" type="password" value={roomUpdatePassword} onChange={(e)=>setRoomUpdatePassword(e.target.value)} />
            </FormControl>
          </ModalBody>
          <ModalFooter>
            <FormControl>
              <Input id='message' placeholder="Message..." name="message" value={message} onChange={(ev)=>setMessage(ev.target.value)} />
            </FormControl>
            <Button data-testid='sendbutton' colorScheme="blue" mr={3} value="send" name='action1' onClick={()=>processUpdates('send')}>
              Send
            </Button>
            {/* <Button data-testid='updatebutton' colorScheme="blue" mr={3} value="update" name='action2' onClick={()=>processUpdates('edit')}>
              Update
            </Button> */}
            {/* <Button onClick={closeChat}>Close</Button> */}
          </ModalFooter>
        </form>
      </ModalContent>
    </Modal>
  </>
}

export default Chat;
