import React, { useCallback, useEffect, useState } from 'react';

import { Socket, io } from 'socket.io-client';

import Draggable from 'react-draggable';

import {
  Button,
  Checkbox,
  FormControl,
  FormLabel,
  Input,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  useToast,
  Table,
  TableCaption,
  Tbody,
  Td,
  Th,
  Thead,
  Tr
} from '@chakra-ui/react';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import useCoveyAppState from '../../hooks/useCoveyAppState';
import Player from '../../classes/Player';
import { MessageFormat } from '../../CoveyTypes';

// import useMaybeVideo from '../../hooks/useMaybeVideo';

const Chat: React.FunctionComponent = () => {
  const {isOpen, onOpen, onClose} = useDisclosure()
//   const video = useMaybeVideo()
  const {socket, messageHistory, players, userName, roomSockets} = useCoveyAppState();
  const [message, setMessage] = useState<string>('');
  // const [isPubliclyListed, setIsPubliclyListed] = useState<boolean>(currentTownIsPubliclyListed);
  const [receiver, setReceiver] = useState<string>('');
  // const [receiver, setReceiver] = useState<Player>();
  const [currentPlayers, setCurrentPlayers] = useState<Player[]>([]);
  // const [currentSockets, setCurrentSockets] = useState<{player: Player, socket: Socket}[]>([]);
  const [currentMessages, setCurrentMessages] = useState<MessageFormat>();


  const openChat = useCallback(()=>{
    onOpen();
  }, [onOpen]);

  const closeChat = useCallback(()=>{
    onClose();
  }, [onClose]);



  const updatePlayers = useCallback(() => {
    setCurrentPlayers(players.sort((a,b) => a.userName < b.userName ? -1:1));
  }, [players]);

  // const updateSockets = useCallback(() => {
  //   setCurrentSockets(roomSockets);
  // }, [roomSockets]);

  const updateMessageHistory = useCallback(() => {
    setCurrentMessages(messageHistory);
  }, [messageHistory]);

  useEffect(() => {
    updatePlayers();
    const timer = setInterval(updatePlayers, 2000);
    const timer2 = setInterval(updateMessageHistory, 2000);

    return () => {
      clearInterval(timer);
      clearInterval(timer2);
    };
  }, [updatePlayers, updateMessageHistory]);

  const processUpdates = async () =>{
    if (receiver === 'Everyone') {
      socket?.emit('sendPublicMessage', message);
      const to = currentPlayers.filter((player) => player.userName !== userName);
      console.log(to[0].userName);
      if (!to[0].socket) {
        console.log('null');
      }
      console.log(io.sockets);
      // console.log(to[0].socket);
      console.log(`socket id${  to[0].socket?.id}`);
      to.forEach((player) => player?.socket?.emit('receivedPublicMessage', message, userName));


      // console.log(roomSockets.length);

      // roomSockets.forEach((s) => s.socket?.emit('receivedPublicMessage', message, userName) ? s.player.userName !== userName:null);
    } else {
      socket?.emit('sendPrivateMessage', message, receiver)
      const toPlayer = roomSockets.find((s) => s.player.userName === receiver);
      toPlayer?.socket.emit('receivedPrivateMessage', message, userName);

    } 
  };

  return <>
    <MenuItem data-testid='openMenuButton' onClick={openChat}>
      <Typography variant="body1">Chat</Typography>
    </MenuItem>
    <Modal isOpen={isOpen} onClose={closeChat}>
      <ModalOverlay/>
      <ModalContent>
        <ModalHeader>Chat</ModalHeader>
        <ModalCloseButton/>
          <ModalBody pb={6}>

            {currentPlayers?.map((player) => ( 
              <p key={player.id}> {player.userName} </p>))}  

            <div>
            {currentMessages?.messageHistory.map((msg) => ( 
              <p key={msg.id}> {msg.from} to {msg.to} <br /> {msg.message}  </p>
            ))} 
            </div>
          </ModalBody>
          <ModalFooter>
          <FormControl>
            <FormLabel htmlFor='friendlyName'>To</FormLabel>
              <Input id='friendlyName' placeholder="To" name="receiver" value={receiver} onChange={(ev)=>setReceiver(ev.target.value)} />
            </FormControl>
            <FormControl>
              <Input id='message' placeholder="Message..." name="message" value={message} onChange={(ev)=>setMessage(ev.target.value)} />
            </FormControl>
            <Button data-testid='sendbutton' colorScheme="blue" mr={3} value="send" name='action1' onClick={()=>processUpdates()}>
              Send
            </Button>
          </ModalFooter>
      </ModalContent>
    </Modal>
  </>
}

export default Chat;
