import { Socket } from "socket.io-client";


export default class Player {

  public location?: UserLocation;

  private readonly _id: string;

  private readonly _userName: string;

  public sprite?: Phaser.GameObjects.Sprite;

  public label?: Phaser.GameObjects.Text;

  public socket: Socket;

  constructor(id: string, userName: string, location: UserLocation, socket: Socket) {
    this._id = id;
    this._userName = userName;
    this.location = location;
    this.socket = socket;
  }

  get userName(): string {
    return this._userName;
  }

  get id(): string {
    return this._id;
  }

  // set socket(s: Socket) {
  //   this.socket = s;
  // }

  static fromServerPlayer(playerFromServer: ServerPlayer): Player {
    return new Player(playerFromServer._id, playerFromServer._userName, playerFromServer.location, playerFromServer.socket);
  }
}
export type ServerPlayer = { _id: string, _userName: string, location: UserLocation, socket: Socket };

export type Direction = 'front'|'back'|'left'|'right';

export type UserLocation = {
  x: number,
  y: number,
  rotation: Direction,
  moving: boolean
};
